@Component
public class StepConfiguration {
@Autowired
JobRepository repository;
@Autowired
TransactionManager trManager;
@Autowired
PaymentsReader reader; @Autowired
PaymentsProcessor processor; @Autowired
PaymentsWriter writer;
@Autowired
PaymentsPartitioner partitioner;
public Step processingPartition() {
return new StepBuilder("processing-step")
.repository(jobRepository)
.partitioner(partitioner)
.step(processingStep())
.gridSize(4)
.taskExecutor(taskExecutor())
.build();
}
private Step processingStep() {
return new StepBuilder("processing-chunk")
.repository(repository)
.<InputObj, OutputObj>chunk(2, trManager)
.reader(reader)

.processor(processor)
.writer(writer)
.build();
}
private TaskExecutor taskExecutor() {
return new SimpleAsyncTaskExecutor();
}

@Configuration
@EnableBatchProcessing
public class ProcessingJobConfig {
@Autowired
private JobRepository repository;
@Autowired
JobCompletionListener jobCompletionListener;
@Autowired
StepConfiguration step;
@Bean
public JobLauncher jobLauncher() throws Exception {
JobLauncher launcher = new TaskExecutorJobLauncher();
launcher.setJobRepository(jobRepository);
launcher.afterPropertiesSet();
return launcher;
}
@Bean("processing-job")
public Job processingJob() {
return new JobBuilder("processing-job", repository)
.incrementer(new RunIdIncrementer())
.listener(jobCompletionListener)
.flow(step.processingPartition())
.end().build();
