@Component
public class GhslFileStepConfiguration {
@Autowired
private JobRepository jobRepository;
@Autowired
private PlatformTransactionManager transactionManager;
@Autowired
GhslFileDataReader ghslFileDataReader;
@Autowired
GhslFileDataWriter ghslFileDataWriter;
@Autowired
GhslFileDataPartitioner partitioner;
public Step downloadingPartition() {
return new StepBuilder("processing-partition")
.repository(jobRepository)
.partitioner("processing", partitioner)
.step(downloadingStep())
.gridSize(4)
.taskExecutor(taskExecutor())
.build();
}
private Step downloadingStep() {
return new StepBuilder("processing-step")
.repository(jobRepository)
.<GhslFileDataItem, GhslFileDataItem> chunk(1, transactionManager)
.reader(ghslFileDataReader)
.writer(ghslFileDataWriter)
.build();
}
private TaskExecutor taskExecutor() {
return new SimpleAsyncTaskExecutor();
}
}

@Service
public class ProcessingJobRunner {
@Autowired
private JobLauncher jobLauncher;
@Autowired
@Qualifier("processing-job")
private Job processingJob;
public void runProcessingJob() throws Exception {
var params = new JobParametersBuilder()
.toJobParameters();
jobLauncher.run(processingJob, params);
}
}

@Configuration
@EnableBatchProcessing
public class PopulationBatchConfig {
@Autowired
private JobRepository jobRepository;
@Autowired
JobCompletionListener jobCompletionListener;
@Autowired
GhslFileStepConfiguration ghslFileStep;
@Bean
public JobLauncher jobLauncher() throws Exception {
var jobLauncher = new TaskExecutorJobLauncher();
jobLauncher.setJobRepository(jobRepository);
jobLauncher.afterPropertiesSet();
return jobLauncher;
}
@Bean("processing-job")
public Job processJob() {
return new JobBuilder("processing-job", jobRepository)
.incrementer(new RunIdIncrementer())
.listener(jobCompletionListener)
.flow(ghslFileStep.downloadingPartition())
.end().build();
}
}
